import React, {useEffect, useState} from 'react';
import {observer} from "mobx-react-lite";
import {useLocation} from "react-router-dom";
import axios from "axios";
import {API_URL} from "../../config";
import "./SinLanPost.css"
import TextareaAutosize from 'react-textarea-autosize';

const SinLanPost = observer(() => {
    const PF = "http://localhost:4000/images/"

    const location = useLocation()
    const path = location.pathname.split("/")[2];

    const [LanPost, setLanPost] = useState({})
    const [title, setTitle] = useState("")
    const [textOne, setTextOne] = useState("")
    const [textTwo, setTextTwo] = useState("")
    const [textThree, setTextThree] = useState("")
    const [linksURL, setLinksURL] = useState("")
    const [exercise, setExercise] = useState("")

    useEffect(() => {
        const getLanPost = async () => {
            const res = await axios.get(`${API_URL}/LanUnderTheTopic/` + path)
            setLanPost(res.data)
            setTitle(res.data.title)
            setTextOne(res.data.textOne)
            setTextTwo(res.data.textTwo)
            setTextThree(res.data.textThree)
            setLinksURL(res.data.linksURL)
            setExercise(res.data.exercise)
        };

        getLanPost()
    }, [path])

    return (
        <div className="MainSinPost">
            <div className="SinPost">
                <div className="DivTitle">
                    <h2>Тема: {title}</h2>
                </div>
                <div className="DivMain">
                    <div className="DivText">{textOne?
                        <div>
                            <div >
                                <img className="IconImg" src="https://cdn-icons-png.flaticon.com/512/259/259523.png"/>
                            </div>
                            <div style={{marginTop: -48, marginLeft: 20}}>
                                <TextareaAutosize align="justify" className="TextareaAuto" value={textOne} autoFocus />
                            </div>
                        </div>
                        :
                        <></>
                    }
                    </div>
                    <div className="DivPhotoClass">
                        {LanPost.photoOne && (
                            <img className="DivPhoto" className="DivPhoto" src={LanPost.photoOne} alt=""/>
                        )}
                    </div>
                    <div className="DivText">{textTwo?
                        <div>
                            <div >
                                <img  className="IconImg" src="https://cdn-icons-png.flaticon.com/512/259/259523.png"/>
                            </div>
                            <div style={{marginTop: -48, marginLeft: 20}}>
                                <TextareaAutosize align="justify" className="TextareaAuto" value={textTwo} autoFocus />
                            </div>
                        </div>
                        :
                        <></>
                    }
                    </div>
                    <div className="DivPhotoClass">
                        {LanPost.photoTwo && (
                            <img className="DivPhoto" src={PF + LanPost.photoTwo} alt=""/>
                        )}
                    </div>
                    <div className="DivText">{textThree?
                        <div>
                            <div >
                                <img  className="IconImg" src="https://cdn-icons-png.flaticon.com/512/259/259523.png"/>
                            </div>
                            <div style={{marginTop: -48, marginLeft: 20}}>
                                <TextareaAutosize align="justify" className="TextareaAuto" value={textThree} autoFocus />
                            </div>
                        </div>
                            :
                            <></>
                        }
                    </div>
                    <div className="DivPhotoClass">
                        {LanPost.photoThree && (
                            <img className="DivPhoto" src={PF + LanPost.photoThree} alt=""/>
                        )}
                    </div>
                    <div className="DivText">{linksURL?
                        <>
                            <p style={{fontFamily:"Comic Sans MS, Comic Sans, cursive"}}><img height={25} width={25} src="https://cdn-icons-png.flaticon.com/512/3778/3778876.png"/><n style={{marginLeft: 10, fontWeight: "bold"}}>Додатковий матеріал</n></p>
                            <TextareaAutosize align="justify" className="TextareaAuto" value={linksURL} autoFocus />
                        </>
                        :
                        <></>
                    }
                    </div>
                </div>
                <div className="DivExercise">
                    <p><img width={25} height={25} src='data:image/png;base64,'/><n style={{marginLeft: 13, fontWeight: "bold"}}>Завдання</n></p>
                    {exercise ?
                        <TextareaAutosize align="justify" className="TextareaAuto" value={exercise} autoFocus />
                        :
                        <></>
                    }
                </div>
            </div>
        </div>
    );
});

export default SinLanPost;